definition.script:
pipeline {
    agent any

    environment {
        API_URL = 'http://localhost:5000'
        FRONTEND_URL = 'http://localhost:3000'
    }

    stages {
        stage('Checkout') {
            steps {
                echo 'üîç Checking out code...'
                checkout scm
            }
        }

        stage('Environment Setup') {
            steps {
                echo 'üîß Setting up test environment...'
                script {
                    sh '''
                        cd /workspace
                        docker-compose up -d
                        sleep 30
                    '''
                }
            }
        }

        stage('Install Dependencies') {
            steps {
                echo 'üì¶ Installing test dependencies...'
                script {
                    sh '''
                        cd /workspace/tests
                        npm install axios
                    '''
                }
            }
        }

        stage('Backend Tests') {
            steps {
                echo 'üî• Running backend baseline tests...'
                script {
                    sh '''
                        cd /workspace/tests
                        node backend-corrected.test.js
                    '''
                }
            }
        }

        stage('Frontend Tests') {
            steps {
                echo 'üé® Running frontend baseline tests...'
                script {
                    sh '''
                        cd /workspace/tests
                        node frontend-simple.test.js
                    '''
                }
            }
        }

        stage('Performance Tests') {
            steps {
                echo '‚ö° Running performance baseline tests...'
                script {
                    sh '''
                        cd /workspace/tests
                        node performance-simple.test.js
                    '''
                }
            }
        }

        stage('All Tests') {
            steps {
                echo 'üß™ Running all baseline tests...'
                script {
                    sh '''
                        cd /workspace/tests
                        node test-runner.js
                    '''
                }
            }
        }
    }

    post {
        always {
            echo 'üßπ Cleaning up...'
            script {
                sh '''
                    cd /workspace
                    docker-compose down
                '''
            }
        }

        success {
            echo '‚úÖ All baseline tests completed successfully!'
        }

        failure {
            echo '‚ùå Baseline tests failed!'
        }
    }
}
